contracts/upgraders/UpgraderBase.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import "../dependencies/openzeppelin/proxy/transparent/ProxyAdmin.sol";
import "../dependencies/openzeppelin//proxy/transparent/TransparentUpgradeableProxy.sol";
import "../interfaces/external/IMulticall.sol";

error StorageValueIsNotEqual();

abstract contract UpgraderBase is ProxyAdmin {
    // Note: `Multicall3` contract has same address for all chains
    // solhint-disable-next-line const-name-snakecase
    address public constant multicall = 0xcA11bde05977b3631167028862bE2a173976CA11;

    /// @inheritdoc ProxyAdmin
    function upgrade(TransparentUpgradeableProxy proxy_, address implementation_) public override onlyOwner {
        bytes[] memory _callsList = _calls();
        bytes[] memory _beforeResults = _aggregate(proxy_, _callsList);

        proxy_.upgradeTo(implementation_);

        bytes[] memory _afterResults = _aggregate(proxy_, _callsList);
        _checkResults(_beforeResults, _afterResults);
    }

    /// @inheritdoc ProxyAdmin
    function upgradeAndCall(
        TransparentUpgradeableProxy proxy_,
        address implementation_,
        bytes calldata data_
    ) public payable override onlyOwner {
        bytes[] memory _callsList = _calls();
        bytes[] memory _beforeResults = _aggregate(proxy_, _callsList);

        TransparentUpgradeableProxy(payable(proxy_)).upgradeToAndCall{value: msg.value}(implementation_, data_);

        bytes[] memory _afterResults = _aggregate(proxy_, _callsList);
        _checkResults(_beforeResults, _afterResults);
    }

    /**
     * @notice Execute storage check calls using `Multicall3` contract
     * @param proxy_ The proxy being upgraded is the target contract
     * @param callDatas_ The array of storage calls to check
     * @return _results The storage values
     */
    function _aggregate(
        TransparentUpgradeableProxy proxy_,
        bytes[] memory callDatas_
    ) private returns (bytes[] memory _results) {
        uint256 _length = callDatas_.length;
        IMulticall.Call[] memory _callsList = new IMulticall.Call[](_length);
        for (uint256 i; i < _length; ++i) {
            _callsList[i].target = address(proxy_);
            _callsList[i].callData = callDatas_[i];
        }
        (, _results) = IMulticall(multicall).aggregate(_callsList);
    }

    /**
     * @notice Return list of storage calls
     * @dev The values of those calls will be compared before and after upgrade to check storage integrity
     */
    function _calls() internal virtual returns (bytes[] memory _callsList);

    /**
     * @notice Compare values
     * @dev Throws if values are inconsistent
     */
    function _checkResults(bytes[] memory before_, bytes[] memory after_) private pure {
        uint256 _length = before_.length;
        for (uint256 i; i < _length; ++i) {
            if (keccak256(before_[i]) != keccak256(after_[i])) revert StorageValueIsNotEqual();
        }
    }
}

contracts/utils/Pauseable.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import "../interfaces/IPauseable.sol";
import "../access/Governable.sol";

error IsPaused();
error IsShutdown();
error IsNotPaused();
error IsNotShutdown();

/**
 * @dev Contract module which allows children to implement an emergency stop
 * mechanism that can be triggered by an authorized account.
 */
abstract contract Pauseable is IPauseable, Governable {
    /// @notice Emitted when contract is turned on
    event Open(address indexed caller);

    /// @notice Emitted when contract is paused
    event Paused(address indexed caller);

    /// @notice Emitted when contract is shuted down
    event Shutdown(address indexed caller);

    /// @notice Emitted when contract is unpaused
    event Unpaused(address indexed caller);

    bool private _paused;
    bool private _everythingStopped;

    /**
     * @dev Throws if contract is paused
     */
    modifier whenNotPaused() {
        if (paused()) revert IsPaused();
        _;
    }

    /**
     * @dev Throws if contract is shutdown
     */
    modifier whenNotShutdown() {
        if (everythingStopped()) revert IsShutdown();
        _;
    }

    /**
     * @dev Throws if contract is not paused
     */
    modifier whenPaused() {
        if (!paused()) revert IsNotPaused();
        _;
    }

    /**
     * @dev Throws if contract is not shutdown
     */
    modifier whenShutdown() {
        if (!everythingStopped()) revert IsNotShutdown();
        _;
    }

    /**
     * @dev If inheriting child is using proxy then child contract can use
     * __Pauseable_init() function to initialization this contract
     */
    // solhint-disable-next-line func-name-mixedcase
    function __Pauseable_init() internal onlyInitializing {
        __Governable_init();
    }

    /**
     * @notice Return `true` if contract is shutdown
     */
    function everythingStopped() public view virtual returns (bool) {
        return _everythingStopped;
    }

    /**
     * @notice Return `true` if contract is paused
     */
    function paused() public view virtual returns (bool) {
        return _paused;
    }

    /**
     * @dev Open contract operations, if contract is in shutdown state
     */
    function open() external virtual whenShutdown onlyGovernor {
        _everythingStopped = false;
        emit Open(msg.sender);
    }

    /**
     * @dev Suspend deposit feature, if contract is not paused.
     */
    function pause() external virtual whenNotPaused onlyGovernor {
        _paused = true;
        emit Paused(msg.sender);
    }

    /**
     * @dev Suspend all features (issue, repay, deposit, withdraw, liquidate and swap), if not already shutdown.
     */
    function shutdown() external virtual whenNotShutdown onlyGovernor {
        _everythingStopped = true;
        _paused = true;
        emit Shutdown(msg.sender);
    }

    /**
     * @dev Unpause contract operations, allow only if contract is paused and not shutdown.
     */
    function unpause() external virtual whenPaused whenNotShutdown onlyGovernor {
        _paused = false;
        emit Unpaused(msg.sender);
    }
}

contracts/utils/RecurringAirdrop.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import "../dependencies/openzeppelin/token/ERC20/utils/SafeERC20.sol";
import "../dependencies/openzeppelin/security/ReentrancyGuard.sol";
import "../dependencies/openzeppelin/utils/cryptography/MerkleProof.sol";
import "../access/Governable.sol";

error NothingToClaim();
error InvalidProof();
error NewMerkleRootSameAsCurrent();
error ProofsFileIsNull();

/**
 * @title Generic Recurring Airdrop contract
 */
contract RecurringAirdrop is ReentrancyGuard, Governable {
    using SafeERC20 for IERC20;

    /// @notice The token to distribute
    IERC20 public immutable token;

    /// @notice The merkle root for the current distribution
    bytes32 public merkleRoot;

    /// @notice The proofs file's IPFS hash
    bytes32 public proofsFileHash;

    /// @notice The timestamp of the latest merkle root update
    uint256 public updatedAt;

    /// @notice The Accumulated amount claimed for a given account
    mapping(address => uint256) public claimed;

    /// @notice Emitted when an account claims reward
    event RewardClaimed(address indexed to, uint256 amount);

    /// @notice Emitted when the merkle root is updated
    event MerkleRootUpdated(bytes32 merkleRoot, uint256 createdAt);

    constructor(IERC20 token_) {
        token = token_;
    }

    /**
     * @notice Claim reward
     * @dev Every tree leaf is a `[account, amount]` tuple, we assume that the `msg.sender` is the account
     * @param amount_ The amount to claim
     * @param proof_ The merkle tree proof for the given leaf
     */
    function claim(uint256 amount_, bytes32[] calldata proof_) external nonReentrant {
        if (merkleRoot == bytes32(0)) revert NothingToClaim();

        bytes32 _leaf = keccak256(abi.encodePacked(msg.sender, amount_));
        if (!MerkleProof.verify(proof_, merkleRoot, _leaf)) revert InvalidProof();

        uint256 _claimable = amount_ - claimed[msg.sender];
        if (_claimable == 0) revert NothingToClaim();

        claimed[msg.sender] += _claimable;

        _transferReward(msg.sender, _claimable);

        emit RewardClaimed(msg.sender, _claimable);
    }

    /**
     * @notice Transfer reward to the user
     * @param to_ The claim account
     * @param amount_ The reward amount
     */
    function _transferReward(address to_, uint256 amount_) internal virtual {
        token.safeTransfer(to_, amount_);
    }

    /**
     * @notice Update merkle tree root
     * @param merkleRoot_ The merkle root
     */
    function updateMerkleRoot(bytes32 merkleRoot_, bytes32 proofsFileHash_) external onlyGovernor {
        if (merkleRoot_ == merkleRoot) revert NewMerkleRootSameAsCurrent();
        if (proofsFileHash_ == bytes32(0)) revert ProofsFileIsNull();

        merkleRoot = merkleRoot_;
        updatedAt = block.timestamp;
        proofsFileHash = proofsFileHash_;

        emit MerkleRootUpdated(merkleRoot_, block.timestamp);
    }
}

